mapper.insert:
insert into student_user (id, username, password, 
  email, phone, real_name, 
  nick_name, sex, class
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=CHAR}, #{password,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{phone,jdbcType=CHAR}, #{realName,jdbcType=VARCHAR}, 
  #{nickName,jdbcType=VARCHAR}, #{sex,jdbcType=TINYINT}, #{class,jdbcType=INTEGER}
  )

mapper.select:
select id, username, password, email, phone, real_name, nick_name, sex, class
from student_user
where id = #{id,jdbcType=INTEGER}
